<style>
	@import url('https://fonts.googleapis.com/css2?family=Plus+Jakarta+Sans:ital,wght@0,200..800;1,200..800&display=swap');

	:global(html) {
		font-family: 'Plus Jakarta Sans', sans-serif;
	}

    @keyframes float {
        0%, 100% { transform: translateY(0) translateX(0) scale(1); }
        50% { transform: translateY(-2px) translateX(-4px) scale(1.02); }
    }

    @keyframes glow {
        0% { filter: drop-shadow(0 0 4px currentColor); }
        50% { filter: drop-shadow(0 0 8px currentColor); }
        100% { filter: drop-shadow(0 0 4px currentColor); }
    }

    .orbit-float {
        animation: float 4s ease-in-out infinite, glow 4s ease-in-out infinite;
    }

    .delay-1 { animation-delay: 0s; }
    .delay-2 { animation-delay: 0.5s; }
    .delay-3 { animation-delay: 1s; }

    @keyframes spin-slow {
        from { transform: rotate(0deg); }
        to { transform: rotate(360deg); }
    }

    .spin-orbit {
        transform-origin: center;
        animation: spin-slow 30s linear infinite;
    }
</style>
